"use strict";
var namaSaya = "bais yusfan";
console.log("nama gwejh", namaSaya);
var umur = 11;
console.log("umur gwejh: ", umur);
var islife = false;
console.log("apakah saya sudah makan: ", islife);
let x = null;
console.log("nilai_x", x);
//non.primitif
//tipedata object
var person = { nama: "Arlot", umur: 10 };
console.log("nama hero saya: ", person);
//array
var numbers = [1, 2, 3, 4, 5];
console.log("nomer urut: ", numbers);
//tuple
var user = ['zilong', 65];
console.log("saya", user);
//any
var random = 25;
console.log("nilai belum diubah", random);
random = "tak ubah ah";
console.log("nilai setelah diubah", random);
//union
var id;
id = 123;
console.log(id);
//for Loop
for (let i = 1; i <= 5; i++) {
    console.log(i);
}
//while loop
let angkat = 1;
while (angkat <= 5) {
    console.log(angkat);
    angkat++;
}
//do while loop
var number = 10;
do {
    console.log(number);
    number++;
} while (number <= 20);
