"use strict";
// if else
var nilai = 75;
if (nilai >= 90) {
    console.log("nilai", nilai, "mendpatkan nilai A");
}
else if (nilai >= 75) {
    console.log("nilai", nilai, "mendapatkan nilai B ");
}
else if (nilai >= 60) {
    console.log("nilai", nilai, "mendapatkan nilai C ");
}
else {
    console.log("nilai", nilai, "mendapatkan nilai D ");
}
